import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row, Col, Pagination } from 'react-bootstrap';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import './App.css';
import './style.css';

function App() {
  const [users, setUsers] = useState([]);
  const [nextLink, setNextLink] = useState('');
  const [prevLink, setPrevLink] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [details, detailsUser] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);
  const [repos, setRepos] = useState([]);
  const [showDetails, setShowDetails] = useState(false);

  useEffect(() => {
    getUsers();
  }, []);

  const getUsers = async (since = '') => {
    try {
      const response = await axios.get(`http://localhost:9000/api/users?since=${since}`);
      setUsers(response.data.users);
      setNextLink(response.data.nextLink);
      setPrevLink(response.data.prevLink);
    } catch (error) {
      console.error(error);
    }
  };

  const handleSearch = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.get(`http://localhost:9000/api/users/${searchTerm}`);
      setSelectedUser(response.data);
      getRepos(response.data.login);
    } catch (error) {
      console.error(error);
    }
  };

  const detailUser = async (username) => {
    try {
      const response = await axios.get(`http://localhost:9000/api/users/${username}`);
      setSelectedUser(response.data);
      detailsUser(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const getRepos = async (username) => {
    try {
      detailUser(username)
      const response = await axios.get(`http://localhost:9000/api/user/repos?username=${username}`);
      setRepos(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDetailsClick = async (user) => {
    setSelectedUser(user);
    getRepos(user.login);
    setShowDetails(!showDetails);
  };

  return (
    <Container fluid>
      <Row className="header">
        <Col>
          <h1>GitHub Users</h1>
        </Col>
        <Col className="search-form">
          <Form onSubmit={handleSearch}>
            <Form.Group controlId="formBasicEmail">
              <Form.Control
                type="text"
                placeholder="Enter username"
                value={searchTerm}
                onChange={(event) => setSearchTerm(event.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Search
            </Button>
          </Form>
        </Col>
      </Row>
      <Row className="content">
        <Col md={8} className="users-table">
          <Table striped bordered hover variant="dark">
            <thead>
              <tr>
                <th>ID</th>
                <th>Login</th>
                <th>Details</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <React.Fragment key={user.id}>
                  <tr>
                    <td>{user.id}</td>
                    <td>{user.login}</td>
                    <td>
                      <Button variant="primary" onClick={() => handleDetailsClick(user)}>
                        {showDetails && selectedUser.id === user.id ? 'Hide Details' : 'Show Details'}
                      </Button>
                    </td>
                  </tr>
                  {selectedUser && selectedUser.id === user.id && showDetails && (
                    <tr>
                      <td colSpan={3}>
                        <Row className="user-details my-custom-class">
                          <Col>
                            <h2>{details.login}</h2>
                            <p>ID: {details.id}</p>
                            <p>Profile URL: {details.html_url}</p>
                            <p>Created At: {details.created_at}</p>

                          </Col>
                        </Row>
                        <Row className="user-details my-custom-class">
                          <Col>
                            <Table striped bordered hover variant="dark">
                              <thead>
                                <tr>
                                  <th>ID</th>
                                  <th>Name</th>
                                  <th>URL</th>
                                </tr>
                              </thead>
                              <tbody>
                                {repos.map((repo) => (
                                  <tr key={repo.id}>
                                    <td>{repo.id}</td>
                                    <td>{repo.name}</td>
                                    <td>
                                      <a href={repo.html_url}>{repo.html_url}</a>
                                    </td>
                                  </tr>
                                ))}
                              </tbody>
                            </Table>
                          </Col>
                        </Row>
                      </td>
                    </tr>
                  )}
                </React.Fragment>
              ))}
            </tbody>
          </Table>
          <Pagination>
  {prevLink && (
    <Pagination.Prev
      onClick={() => getUsers(prevLink.split('=')[1])}
      style={{ backgroundColor: '#007bff' }}
    />
  )}
  {nextLink && (
    <Pagination.Next
      onClick={() => getUsers(nextLink.split('=')[1])}
      style={{ backgroundColor: '#007bff', color: '#fff' , width: "80px", fontSize: "30px" ,}}
    />
  )}
</Pagination>

        </Col>
      </Row>
    </Container>
  );
}

export default App;
